import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash/debounce';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogFooter 
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2 } from "lucide-react";

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [updatedProduct, setUpdatedProduct] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const navigate = useNavigate();

  const fetchData = useCallback(async (token) => {
    try {
      const response = await axios.get('/products', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && Array.isArray(response.data.products)) {
        setProducts(response.data.products);
        setFilteredProducts(response.data.products);
      } else {
        throw new Error('Format data tidak sesuai');
      }
    } catch (error) {
      if (axios.isAxiosError(error) && error.response?.status === 401) {
        setError('Sesi Anda telah berakhir. Silakan login kembali.');
        localStorage.removeItem('token');
        navigate('/');
      } else {
        setError(error.message || 'Terjadi kesalahan saat memuat data');
      }
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    } else {
      fetchData(token);
    }
  }, [navigate, fetchData]);

  const handleSearch = useCallback(
    debounce((term) => {
      const filtered = products.filter(product =>
        product.productName.toLowerCase().includes(term.toLowerCase()) ||
        product.category.toLowerCase().includes(term.toLowerCase())
      );
      setFilteredProducts(filtered);
    }, 300),
    [products]
  );

  useEffect(() => {
    handleSearch(searchTerm);
  }, [searchTerm, handleSearch]);

  const handleEdit = (product) => {
    setCurrentProduct(product);
    setUpdatedProduct({ ...product });
    setIsEditModalOpen(true);
  };

  const handleQuantityChange = (e) => {
    const quantity = parseInt(e.target.value) || 0;
    const totalPrice = updatedProduct.price * quantity;
    setUpdatedProduct({ ...updatedProduct, quantity, totalPrice });
  };

  const handleUpdateProduct = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      await axios.put(`/products/${currentProduct._id}`, updatedProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setIsEditModalOpen(false);
      fetchData(token);
    } catch (error) {
      setError('Terjadi kesalahan saat mengupdate produk.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus produk ini?')) {
      try {
        setIsLoading(true);
        const token = localStorage.getItem('token');
        await axios.delete(`/products/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        fetchData(token);
      } catch (error) {
        setError('Terjadi kesalahan saat menghapus produk.');
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  const sortedProducts = React.useMemo(() => {
    let sortableProducts = [...filteredProducts];
    if (sortConfig.key !== null) {
      sortableProducts.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableProducts;
  }, [filteredProducts, sortConfig]);

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen"><Loader2 className="animate-spin" /></div>;
  }

  if (error) {
    return <Alert variant="destructive"><AlertDescription>{error}</AlertDescription></Alert>;
  }

  return (
    <div className="container mx-auto p-4">
      <Input
        type="text"
        placeholder="Cari produk..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="mb-4"
      />
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead onClick={() => handleSort('productName')}>Nama Produk</TableHead>
            <TableHead onClick={() => handleSort('category')}>Kategori</TableHead>
            <TableHead onClick={() => handleSort('quantity')}>Jumlah</TableHead>
            <TableHead onClick={() => handleSort('price')}>Harga Satuan</TableHead>
            <TableHead onClick={() => handleSort('totalPrice')}>Total Harga</TableHead>
            <TableHead onClick={() => handleSort('date')}>Tanggal</TableHead>
            <TableHead>Gambar</TableHead>
            <TableHead>Aksi</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {sortedProducts.map((product) => (
            <TableRow key={product._id}>
              <TableCell>{product.productName}</TableCell>
              <TableCell>{product.category}</TableCell>
              <TableCell>{product.quantity}</TableCell>
              <TableCell>Rp {product.price.toLocaleString()}</TableCell>
              <TableCell>Rp {product.totalPrice.toLocaleString()}</TableCell>
              <TableCell>{new Date(product.date).toLocaleDateString()}</TableCell>
              <TableCell>
                {product.image && (
                  <img
                    src={`https://inventaris-app-backend.vercel.app${product.image}`}
                    alt={product.productName}
                    className="w-24 h-24 object-cover"
                  />
                )}
              </TableCell>
              <TableCell>
                <Button onClick={() => handleEdit(product)} className="mr-2">Edit</Button>
                <Button variant="destructive" onClick={() => handleDelete(product._id)}>Delete</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Produk</DialogTitle>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="name" className="text-right">
                Nama Produk
              </Label>
              <Input
                id="name"
                value={updatedProduct.productName || ''}
                onChange={(e) => setUpdatedProduct({ ...updatedProduct, productName: e.target.value })}
                className="col-span-3"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="category" className="text-right">
                Kategori
              </Label>
              <Input
                id="category"
                value={updatedProduct.category || ''}
                onChange={(e) => setUpdatedProduct({ ...updatedProduct, category: e.target.value })}
                className="col-span-3"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="quantity" className="text-right">
                Jumlah
              </Label>
              <Input
                id="quantity"
                type="number"
                value={updatedProduct.quantity || ''}
                onChange={handleQuantityChange}
                className="col-span-3"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="totalPrice" className="text-right">
                Total Harga
              </Label>
              <Input
                id="totalPrice"
                value={`Rp ${updatedProduct.totalPrice?.toLocaleString() || ''}`}
                disabled
                className="col-span-3"
              />
            </div>
          </div>
          <DialogFooter>
            <Button onClick={handleUpdateProduct}>Update</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default Dashboard;
