import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { debounce } from 'lodash'; // Import debounce for optimizing search input
import './dashboard.css';

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState(''); // Search term state
  const navigate = useNavigate();

  const fetchData = useCallback(async (token) => {
    try {
      const response = await axios.get('/products', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && Array.isArray(response.data.products)) {
        setProducts(response.data.products);
      } else {
        throw new Error('Data format error');
      }
    } catch (error) {
      setError(error.message || 'Error loading data');
      if (axios.isAxiosError(error) && error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchData(token);
    } else {
      navigate('/');
    }
  }, [fetchData, navigate]);

  // Debounced search function to filter products based on searchTerm
  const debouncedSearch = useCallback(
    debounce((term) => {
      const filtered = products.filter(product =>
        product.productName.toLowerCase().includes(term.toLowerCase()) ||
        product.category.toLowerCase().includes(term.toLowerCase())
      );
      setProducts(filtered);
    }, 300),
    [products]
  );

  useEffect(() => {
    if (searchTerm) {
      debouncedSearch(searchTerm);
    } else {
      fetchData(localStorage.getItem('token'));
    }
  }, [searchTerm, debouncedSearch, fetchData]);

  if (isLoading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="dashboard-container">
      <input
        type="text"
        placeholder="Search products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)} // Search input field
        className="search-input m-3"
      />
      {/* Existing code for displaying products */}
    </div>
  );
};

export default Dashboard;
